  `push_token` varchar(64) DEFAULT NULL COMMENT '推送使用的Token',
  `wx_openid` varchar(64) DEFAULT '' COMMENT '微信openid',
  `recommend_code` varchar(32) DEFAULT NULL COMMENT '推荐码',
  `create_at` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_at` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=30 DEFAULT CHARSET=utf8 COMMENT='货主基本用户表';

