-- ----------------------------
-- Table structure for `rt_transport_order`
-- ----------------------------
DROP TABLE IF EXISTS `rt_transport_order`;
CREATE TABLE `rt_transport_order` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `order_code` varchar(255) DEFAULT NULL COMMENT '订单号',
  `goods_id` int(11) DEFAULT NULL COMMENT '货源ID',
  `sp_id` int(11) unsigned NOT NULL COMMENT '参考 sp_baseinfo',
  `dr_id` int(11) DEFAULT NULL COMMENT '接单司机ID',
  `type` varchar(32) NOT NULL DEFAULT 'often' COMMENT 'often-实时-默认\r\nurgent-加急\r\nappoint-预约',
  `appoint_at` int(11) DEFAULT NULL COMMENT '预约时间',
  `insured_amount` decimal(10,2) DEFAULT NULL COMMENT '投保金额',
  `premium_amount` decimal(10,2) DEFAULT NULL COMMENT '保费金额',
  `org_send_name` varchar(50) NOT NULL COMMENT '发货人姓名',
  `org_address_maps` varchar(30) NOT NULL COMMENT '出发地地址的坐标 如116.480881,39.989410',
  `org_city` varchar(30) NOT NULL COMMENT '发货人省市区',
  `org_address_name` varchar(255) NOT NULL COMMENT '发货人出发地名称',
  `org_address_detail` varchar(255) DEFAULT NULL COMMENT '发货人地址详情',
  `org_phone` varchar(50) NOT NULL COMMENT '发货人手机',
  `org_telphone` varchar(50) DEFAULT NULL COMMENT '发货人电话',
  `dest_receive_name` varchar(255) NOT NULL COMMENT '收货人姓名',
  `dest_address_maps` varchar(30) NOT NULL COMMENT '目的地址的坐标 如116.480881,39.989410',
  `dest_city` varchar(50) NOT NULL COMMENT '到达城市',
  `dest_address_name` varchar(255) NOT NULL COMMENT '目的地名称',
  `dest_address_detail` varchar(255) DEFAULT NULL COMMENT '目的地详情',
  `dest_phone` varchar(50) NOT NULL COMMENT '收货人手机',
  `dest_telphone` varchar(50) DEFAULT NULL COMMENT '收货人电话',
  `goods_name` varchar(50) NOT NULL COMMENT '货物名称',
  `volume` float(10,3) NOT NULL COMMENT '总体积（立方米）',
  `weight` float(10,3) NOT NULL COMMENT '总重量（吨）',
  `car_style_type` varchar(30) NOT NULL COMMENT '车型名称-对应car_style里name',
  `car_style_type_id` int(11) NOT NULL DEFAULT '0' COMMENT '车型-对应car_style里id，默认0全部',
  `car_style_length` varchar(30) NOT NULL COMMENT '车长-对应car_style里name',
  `car_style_length_id` int(11) NOT NULL DEFAULT '0' COMMENT '车长-对应car_style里id，默认0，全部',
  `effective_time` int(10) DEFAULT NULL COMMENT '在途时效',
  `is_receipt` tinyint(1) NOT NULL DEFAULT '1' COMMENT '货物回单1-是-默认，2-否',
  `mind_price` decimal(10,2) DEFAULT NULL COMMENT '心理价位',
  `final_price` decimal(10,2) DEFAULT NULL COMMENT '最终报价，订单需支付价格',
  `system_price` decimal(10,2) NOT NULL COMMENT '系统价',
  `payway` tinyint(1) DEFAULT '0' COMMENT '0=未支付，1=余额，2=微信，3=支付宝，4-凭证通过',
  `is_pay` tinyint(1) DEFAULT '0' COMMENT '是否支付0-未支付，1-支付',
  `remark` varchar(100) DEFAULT NULL COMMENT '备注',
  `arr_cer_pic` varchar(500) DEFAULT '' COMMENT '到货凭证',
  `pay_cer_pic` varchar(500) DEFAULT NULL COMMENT '付款凭证',
  `tran_type` tinyint(1) DEFAULT '0' COMMENT '0=短途1=长途',
  `create_at` int(11) DEFAULT NULL,
  `update_at` int(11) DEFAULT NULL,
  `status` varchar(32) DEFAULT 'quote' COMMENT '订单状态：\r\nquote报价中\r\nquoted已报价-未配送（装货中）\r\ndistribute配送中（在配送-未拍照）发货中\r\nphoto 拍照完毕（订单已完成）\r\npay_failed（支付失败）/pay_success（支付成功）\r\ncomment（已评论）\r\n\r\n\r\n\r\n',
  `per_status` varchar(255) DEFAULT 'init' COMMENT 'init初始状态\r\n，check=认证中,pass审核通过，并更改status为pay_suceess\r\nrefuse审核拒绝，并更改status为pay_failed',
  `is_beyond_quota` tinyint(1) DEFAULT '0' COMMENT '0未超出\r\n1超出限额',
  `pay_time` int(11) DEFAULT NULL COMMENT '支付订单的日期',
  `send_time` int(11) DEFAULT NULL COMMENT '发货时间',
  `usecar_time` int(11) DEFAULT NULL COMMENT '用车时间',
  `arr_time` int(11) DEFAULT NULL COMMENT '到达时间',
  `policy_code` varchar(255) DEFAULT NULL COMMENT '保单号',
  `per_remark` varchar(255) DEFAULT '' COMMENT '人工审核付款截图说明',
  `real_name` varchar(255) DEFAULT '' COMMENT '货主名',
  `company_name` varchar(255) DEFAULT NULL COMMENT '公司名称',
  `is_clear` tinyint(255) DEFAULT '0' COMMENT '是否结算0-未结算，1-已结算',
  `clear_price` decimal(10,2) DEFAULT NULL COMMENT '结算金额',
  `customer_type` varchar(50) DEFAULT NULL COMMENT 'person-个人\r\ncompany-公司',
  `kilometres` varchar(100) DEFAULT NULL COMMENT '公里数',
  `org_longitude` varchar(50) DEFAULT NULL COMMENT '出发地经度',
  `org_latitude` varchar(50) DEFAULT NULL COMMENT '出发地纬度',
  `dest_longitude` varchar(50) DEFAULT NULL COMMENT '出发地经度',
  `dest_latitude` varchar(50) DEFAULT NULL COMMENT '出发地纬度',
  PRIMARY KEY (`id`),
  UNIQUE KEY `goods_id` (`goods_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=47 DEFAULT CHARSET=utf8 COMMENT='运输订单表';

-- ----------------------------
-- Records of rt_transport_order
