-- ----------------------------
-- Table structure for `rt_withdraw`
-- ----------------------------
DROP TABLE IF EXISTS `rt_withdraw`;
CREATE TABLE `rt_withdraw` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `withdraw_code` varchar(50) DEFAULT NULL COMMENT '提现订单号  ',
  `base_id` int(11) unsigned DEFAULT NULL COMMENT 'sp_baseinfo dr_baseinfo 对应id',
  `merchant_orderid` varchar(50) DEFAULT NULL COMMENT '商户订单号-流水号',
  `real_name` varchar(30) DEFAULT NULL COMMENT '真实姓名',
  `phone` varchar(30) DEFAULT NULL COMMENT '电话',
  `type` varchar(255) DEFAULT 'person' COMMENT 'person=个人货主, company=公司货主， driver=司机端',
  `amount` decimal(10,2) DEFAULT '0.00' COMMENT '提现金额',
  `status` varchar(20) DEFAULT 'init' COMMENT 'init=未处理，agree=后台同意，refuse=已拒绝，pay_success=银行返回成功，pay_fail=银行返回失败',
  `real_amount` decimal(10,2) DEFAULT '0.00' COMMENT '实际到账金额',
  `balance` decimal(10,2) DEFAULT NULL COMMENT '提现之后的金额',
  `remark` varchar(200) DEFAULT NULL COMMENT '处理结果',
  `deposit_name` varchar(30) DEFAULT NULL COMMENT '开户名称',
  `bank` varchar(50) DEFAULT '' COMMENT '开户行',
  `account` varchar(50) DEFAULT NULL COMMENT '银行账号',
  `bank_person_name` varchar(30) DEFAULT NULL COMMENT '开户人',
  `result_info` varchar(200) DEFAULT NULL COMMENT '银行返回的信息',
  `result_time` int(11) DEFAULT NULL COMMENT '银行返回提现结果时间',
  `create_at` int(11) DEFAULT NULL,
  `update_at` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COMMENT='提现表 （货主端和司机端）';

